import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import * as movieAPI from '../services/movieAPI';
import { Loading } from '../components';

class MovieDetails extends Component {
 constructor (props) {
   super(props)
   this.movieDetails = this.movieDetails.bind(this);
 }

 async movieDetails () {
    return await movieAPI.getMovie(this.props.match.params.id)
  }
  componentDidUpdate () {
    this.movieDetails();
  }
  render() {
    // Change the condition to check the state
    // if (true) return <Loading />;

    const { id } = this.props.match.params

    const { title, storyline, imagePath, genre, rating, subtitle } = this.movieDetails();

    return (
      <div data-testid="movie-details">
        <img alt="Movie Cover" src={`../${imagePath}`} />
        <p>{`Title:${title}`}</p>
        <p>{`Subtitle: ${subtitle}`}</p>
        <p>{`Storyline: ${storyline}`}</p>
        <p>{`Genre: ${genre}`}</p>
        <p>{`Rating: ${rating}`}</p>
        <Link to={`/movies/${Number(id)}/edit`} >EDITAR</Link> 
        <Link to="/" >VOLTAR</Link>
      </div>
    );
  }
}

export default MovieDetails;
