import { GET_CURRENCE, REQUEST_CURRENCE, FAILED_REQUEST } from '../actions/index'

const initialState = {
    currencies: [],
    expenses: [],
    isFetching: false
}

const wallet = (state = initialState, action) => {
    switch (action.type) {
        case REQUEST_CURRENCE:
            return { ...state, isFetching: true };
            case GET_CURRENCE:
                return {...state , isFetching:false , currencies: action.payload}
    }
}
