import React from 'react'

class InputEmail extends React.Component {
    constructor(props) {
        super(props)
        this.state={
            email:'',
        }
    }

    changeEmail ({target}) {
        const  {value} = target
        this.setState({
            email: value
        })
    }
    render () {
        return (
         <input onChange={(target) => this.changeEmail(target)} data-testid="email-input" type='email' />
        )
    }
}

export default InputEmail;
