import React from 'react';
import { connect } from 'react-redux';
import { fetchCurrences } from '../actions/index'

class FormDespesas extends React.Component {
    constructor(props) {
           super(props);
           this.state = {
            id: 0,
            value: 0,
            description: '',
            currency: 'USD',
            method: 'Dinheiro',
            tag: 'Alimentação',
            exchangeRates: {},
            edit:false,
          };
    }

    componentDidMount() {
        const { requestCurrence } = this.props;
        requestCurrence()
    }
    render() {
        const { sigleCurrence } = this.props
        const currenceFilter = sigleCurrence.filter((item) => item.name !== 'Dolar Turismo')
        return (
            <form>
                <label htmlFor="valor" >Valor:
              <input id="valor" data-testid="value-input" />
                </label>
                <label htmlFor="descricao" >Descrição:
              <input id="descricao" data-testid="description-input" />
                </label>
                <label htmlFor="moeda" >Moeda:
              <select id="moeda" data-testid="currency-input" >
                        {currenceFilter.map((currence) => {
                            return (
                                <option data-testid={currence.code} >
                                    {currence.code}
                                </option>
                            )
                        })}
                    </select>
                </label>
                <labe>Método de pagamento:
                    <select data-testid="method-input" >
                        <option value="Dinheiro<" >Dinheiro</option>
                        <option value="Cartão de crédito" >Cartão de crédito</option>
                        <option value="Cartão de débito" >Cartão de débito</option>
                    </select>
                </labe>
                <label>Categoria:
                    <select data-testid="tag-input"  >
                        <option value="Alimentação" >Alimentação</option>
                        <option value="Lazer" >Lazer</option>
                        <option value="Trabalho" >Trabalho</option>
                        <option value="Transporte" >Transporte</option>
                        <option value="Saúde" >Saúde</option>
                    </select>
                </label>
                <button>Adicionar despesa</button>
            </form>
        )
    }
}

const mapStateToProps = (state) => ({
    sigleCurrence: state.wallet.currencies
})

const mapDispatchToProps = (dispatch) => ({
    requestCurrence: () => dispatch(fetchCurrences()),
})


export default connect(mapStateToProps, mapDispatchToProps)(FormDespesas);
