// Esse reducer será responsável por tratar as informações da pessoa usuária
import { ADD_EMAIL } from '../actions/index'
const initialState = {
    email:''
}


const user =(state = initialState , action) => {
    switch(action.type) {
        case ADD_EMAIL:
            return {email:action.email}
            default:
                return state
    }
}

export default user;
