import React from 'react';
import { connect } from 'react-redux';
import './HeaderWallet.css';
class HeaderWallet extends React.Component {
    render() {
        const { email , sumExpenses } = this.props

        const resultExpenses = sumExpenses.reduce((acc , reduce) => acc + reduce, 0)
        return (
            <header>
                <p data-testid="email-field" >Email:{email}</p>
                <p data-testid="total-field" >Total: {resultExpenses}</p>
                <p data-testid="header-currency-field" >BRL</p>
            </header>
        )
    }
}
const mapStateToProps = (state) => ({
    email: state.user.email,
    sumExpenses: state.wallet.sumExpenses
})

export default connect(mapStateToProps)(HeaderWallet);
