import React from 'react';
import { connect } from 'react-redux';
import {  sumExpense  } from '../actions/index'


class TableInfo extends React.Component {
 constructor(props) {
     super(props);

     this.state = {
         deleted:false,
     }

     this.deleteExpense = this.deleteExpense.bind(this);
 }



    deleteExpense(index) {
        const { expenses } = this.props
        expenses.splice(index, 1)
        this.setState({
            deleted:true
        })

    }

    render() {
        const { expenses } = this.props

        return (

            <table border="2" >
                <thead>
                    <tr>
                        <td>Descrição</td>
                        <td>Tag</td>
                        <td>Método de pagamento</td>
                        <td>Valor</td>
                        <td>Moeda</td>
                        <td>Câmbio utilizado</td>
                        <td>Valor convertido</td>
                        <td>Moeda de conversão</td>
                        <td>Editar/Excluir</td>
                    </tr>
                </thead>
                {expenses.map((item, index) => {
                    const { currency, exchangeRates } = item
                    const convert = (exchangeRates[currency].ask * item.value).toLocaleString('pt-br', { style: 'currency', currency: 'BRL' });
                    return (
                        <tbody key={index} >
                            <tr>
                                <td>{item.description}</td>
                                <td>{item.tag}</td>
                                <td>{item.method}</td>
                                <td>{item.value}</td>
                                <td>{exchangeRates[currency].name}</td>
                                <td>{Number(exchangeRates[currency].ask).toFixed(2)}</td>
                                <td>{convert}</td>
                                <td>Real</td>
                                <td>
                                    <button type="button" onClick={() => this.deleteExpense(index)} >Deletar</button>
                                    <button type="button">Editar</button>
                                </td>
                            </tr>
                        </tbody>
                    )
                })}


            </table>
        )
    }
}

const mapStateToProps = (state) => ({
    expenses: state.wallet.expenses
})

const mapDispatchToProps = (dispatch) => ({
    sumExpenses: sum => dispatch(sumExpense(sum)),

})

export default connect(mapStateToProps, mapDispatchToProps)(TableInfo);
