import React from 'react';
import { connect } from 'react-redux';
import './HeaderWallet.css';
import { fetchCurrences } from '../actions/index'

class HeaderWallet extends React.Component {


    componentDidMount() {
        const { requestCurrence } = this.props;
        requestCurrence()
    }
    render() {
        const { email } = this.props
        return (
            <header>

                <p data-testid="email-field" >Email:{email}</p>
                <p data-testid="total-field" >Total: 0</p>
                <p data-testid="header-currency-field" >BRL</p>
            </header>
        )
    }
}
const mapStateToProps = (state) => ({
    email: state.user.email,
    sigleCurrence: state.wallet.currencies

})

const mapDispatchToProps = (dispatch) => ({
    requestCurrence: () => dispatch(fetchCurrences()),
})


export default connect(mapStateToProps, mapDispatchToProps)(HeaderWallet);
